$(function(){var j=$("#hover");var m=$("#footer");var d=$("#screen_info");var b=$(".navbar");var o=$(".arrow-up");var g=$(".credit");var k=$(".panel");b.hide();k.draggable({handle:".panel-heading",scroll:false,containment:"window"});k.resizable({handles:"se"});j.mouseenter(function(){b.finish().fadeIn(200);o.finish().fadeOut(200)});j.mouseleave(function(){b.finish().delay(100).fadeOut(200);o.finish().fadeOut(200)});o.delay(3000).fadeOut(3000);m.mouseenter(function(){g.finish().fadeIn(200)});m.mouseleave(function(){g.finish().fadeOut(3000)});g.delay(3000).fadeOut(3000);k.mousemove(function(i){d.text(k.width()+"✕"+k.height()+" — "+k.offset().left+"x "+k.offset().top+"y");if(supports_html5_storage()){localStorage.setItem("editor.x",k.offset().left);localStorage.setItem("editor.y",k.offset().top);localStorage.setItem("editor.w",k.width());localStorage.setItem("editor.h",k.height())}});if(supports_html5_storage()){console.log("window.localStorage is available!");var c=localStorage.getItem("editor.x");var a=localStorage.getItem("editor.y");var e=localStorage.getItem("editor.w");var l=localStorage.getItem("editor.h");for(var h=0;h<localStorage.length;h++){var n=localStorage.key(h);var f=localStorage.getItem(n);console.log("\t"+n+"="+f)}k.css({position:"absolute",top:a+"px",left:c+"px",width:e+"px",height:l+"px"}).show();d.text(e+"✕"+l+" — "+c+"x "+a+"y")}else{console.log("no native support for HTML5 storage :(");d.text("Editor")}k.hover(function(){k.css("z-index","0");k.removeClass("panel-default").addClass("panel-muted");$(this).css("z-index","1");$(this).removeClass("panel-muted").addClass("panel-default")})});