$(function(){var k=$("#hover");var n=$("#footer");var d=$("#screen_info");var c=$(".navbar");var q=$(".arrow-up");var g=$(".credit");var l=$(".panel");var h=$(".editor-input");c.hide();l.draggable({handle:".panel-heading",scroll:false,containment:"window"});l.resizable({handles:"se"});k.mouseenter(function(){c.finish().fadeIn(200);q.finish().fadeOut(200)});k.mouseleave(function(){c.finish().delay(100).fadeOut(200);q.finish().fadeOut(200)});q.delay(3000).fadeOut(3000);n.mouseenter(function(){g.finish().fadeIn(200)});n.mouseleave(function(){g.finish().fadeOut(3000)});g.delay(3000).fadeOut(3000);l.mousemove(function(i){d.text(l.width()+"✕"+l.height()+" — "+l.offset().left+"x "+l.offset().top+"y");if(use_storage()){localStorage.setItem("editor.x",l.offset().left);localStorage.setItem("editor.y",l.offset().top);localStorage.setItem("editor.w",l.width());localStorage.setItem("editor.h",l.height())}});h.change(function(i){if(use_storage()){localStorage.setItem("editor.input",h.val())}});$(".panel-heading").dblclick(function(i){l.css({position:"absolute",top:"1%",left:"1%",width:"98%","max-width":"98%",height:"98%","max-height":"98%"}).show()});if(use_storage()){console.log("window.localStorage is available!");reset=$.urlParam("reset");if(reset=="true"){console.log("Reset window.localStorage");localStorage.setItem("editor.x",100);localStorage.setItem("editor.y",100);localStorage.setItem("editor.w",360);localStorage.setItem("editor.h",240);localStorage.setItem("editor.input","");window.location="http://demo.idleberg.com/fl_Ed/"}else{var b=$.urlParam("editor.x");var a=$.urlParam("editor.y");var e=$.urlParam("editor.w");var m=$.urlParam("editor.h");if(b){localStorage.setItem("editor.x",b)}if(a){localStorage.setItem("editor.y",a)}if(e){localStorage.setItem("editor.w",e)}if(m){localStorage.setItem("editor.h",m)}}var b=localStorage.getItem("editor.x");var a=localStorage.getItem("editor.y");var e=localStorage.getItem("editor.w");var m=localStorage.getItem("editor.h");var p=localStorage.getItem("editor.input");for(var j=0;j<localStorage.length;j++){var o=localStorage.key(j);var f=localStorage.getItem(o);console.log("\t"+o+"="+f)}l.css({position:"absolute",top:a+"px",left:b+"px",width:e+"px",height:m+"px"}).show();d.text(e+"✕"+m+" — "+b+"x "+a+"y");h.val(p)}else{console.log("no native support for HTML5 storage :(");d.text("Editor")}l.hover(function(){l.css("z-index","0");l.removeClass("panel-default").addClass("panel-muted");$(this).css("z-index","1");$(this).removeClass("panel-muted").addClass("panel-default")})});